import {Component} from 'react'
import logo from './logo.svg';
import './App.css';
import CardList from './components/card-list/card-list.component';

class App extends Component {

  constructor(){
    super();
    this.state = {
      monsters: [], 
      searchValue: ''
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((resolveData) => {
      return resolveData.json()
    }) 
    .then((users)=> {
      this.setState(() => {
        return {monsters: users}
      }, () => {
        return this.state.monsters
      })
    })
  }

  onSearchChange = (event) => {
    const searchString = event.target.value.toLocaleLowerCase()

    this.setState(() => {
     return {searchValue: searchString}
    })

  }


  render(){

    const {monsters, searchValue} = this.state; 
    const {onSearchChange} = this; 

    const updatedSearchValue = monsters.filter((monster) => {
      return monster.name.toLocaleLowerCase().includes(searchValue)
    })

    return <div className='App'>
      <input 
      className="search-box" 
      placeholder="Search" 
      type="text" 
      onChange={onSearchChange}/>

      {updatedSearchValue.map((monster) => {
        return <h1 key={monster.id}>{monster.name}</h1>
      })}

      <CardList/>

    </div>

  }

}

export default App;
