import {Component} from 'react'
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();
    this.state = {
      name: {firstName: 'Udin', lastName: 'Rajkanrikar'}, company: 'Vulkerine'
    }
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Hi ! My name is {this.state.name.firstName} {this.state.name.lastName} and I work at {this.state.company}
          </p>
          <button
          onClick={ () => {
            this.setState(
              () => {
                return {
                  name: {firstName: 'Udyn', lastName:'Rajkarnikar1'}, 
                  company: 'Vulkerine3'
                }
              }, 
              () => {
                console.log(this.state) //callback function which runs only after the state is finalized
              })
          }
          }
          >Change Name</button>
        </header>
      </div>
    );
  }

}

export default App;
