import {Component} from 'react'
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();
    this.state = {
      monsters: []
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((resolveData) => {
      return resolveData.json()
    }) 
    .then((users)=> {
      this.setState(() => {
        return {monsters: users}
      }, () => {
        return this.state.monsters
      })
    })
  }

  render(){
    return <div className='App'>

      <input 
      className="search-box" 
      placeholder="Seacrh" 
      type="text" 
      onChange={(event) => {
        const seacrhString = event.target.value.toLocaleLowerCase()
        const updatedSearchValue = this.state.monsters.filter((monster) => {
          return monster.name.toLocaleLowerCase().includes(seacrhString)
        })

        this.setState(() => {
         return {monsters: updatedSearchValue}
        })

      }}/>

      {this.state.monsters.map((monster) => {
        return <h1 key={monster.id}>{monster.name}</h1>
      })}

    </div>

  }

}

export default App;
